var wrap=document,menu=document.querySelector(".menu-container"),menuLink=document.querySelector(".burger"),exit=document.querySelector(".close"),mainBody=document.querySelector("body"),searchButton=document.querySelector(".search"),search=document.querySelector(".search-container"),inputSearch=document.querySelector(".search-input");menuLink.addEventListener("click",function(e){e.preventDefault(),menu.classList.add("menu-container-active")}),exit.addEventListener("click",function(e){e.preventDefault(),menu.classList.remove("menu-container-active")}),wrap.addEventListener("click",function(e){e.target!=document.getElementById("toggle-search")?search.classList.remove("search-container-active"):(search.classList.add("search-container-active"),inputSearch.focus())});